var tar = require('tar');
var fs = require('fs');
var zlib = require('zlib');

fs.createReadStream(__dirname + "/dist/1.8.0.tgz")
  .pipe(zlib.createUnzip())
  .pipe(tar.Parse())
  .on('entry', function(e) {
    console.log(e.props.path);

    var filename = e.props.path;
    if (filename == '1.8.0/jquery-ajax-css-deprecated-dimensions-effects-offset.js') {
      var chunk = '';
      e.on('data', function(c) {
        chunk += c;
      });
      e.on('end', function() {
        console.log(chunk);
      });

    }

  });
